:root {
  --global-font-size: 20px;
  --global-bg: #fff;
  --font-color: #4c4948;
  --hr-border: #a4d8fa;
  --hr-before-color: #80c8f8;
  --search-bg: #f6f8fa;
  --search-input-color: #4c4948;
  --search-a-color: #4c4948;
  --preloader-bg: #37474f;
  --preloader-color: #fff;
  --tab-border-color: #f0f0f0;
  --tab-botton-bg: #f0f0f0;
  --tab-botton-color: #1f2d3d;
  --tab-button-hover-bg: #dcdcdc;
  --tab-button-active-bg: #fff;
  --card-bg: #fff;
  --card-meta: #858585;
  --sidebar-bg: #f6f8fa;
  --sidebar-menu-bg: #fff;
  --btn-hover-color: #ff7242;
  --btn-color: #fff;
  --btn-bg: #49b1f5;
  --text-bg-hover: rgba(73,177,245,0.7);
  --light-grey: #eee;
  --dark-grey: #cacaca;
  --white: #fff;
  --text-highlight-color: #1f2d3d;
  --blockquote-color: #6a737d;
  --blockquote-bg: rgba(73,177,245,0.1);
  --reward-pop: #f5f5f5;
  --toc-link-color: #666261;
  --card-box-shadow: 0 3px 8px 6px rgba(7,17,27,0.05);
  --card-hover-box-shadow: 0 3px 8px 6px rgba(7,17,27,0.09);
  --pseudo-hover: #ff7242;
  --headline-presudo: #a0a0a0;
  --scrollbar-color: #49b1f5;
  --default-bg-color: #49b1f5;
  --zoom-bg: #fff;
  --mark-bg: rgba(0,0,0,0.3);
}
body {
  position: relative;
  overflow-y: scroll;
  min-height: 100%;
  background: var(--global-bg);
  color: var(--font-color);
  font-size: var(--global-font-size);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Helvetica Neue', Lato, Roboto, 'PingFang SC', 'Microsoft YaHei', sans-serif;
  line-height: 2;
  -webkit-tap-highlight-color: rgba(0,0,0,0);
  scroll-behavior: smooth;
}
@-moz-document url-prefix() {
  * {
    scrollbar-width: thin;
    scrollbar-color: var(--scrollbar-color) transparent;
  }
}
*::-webkit-scrollbar {
  width: 5px;
  height: 5px;
}
*::-webkit-scrollbar-thumb {
  background: var(--scrollbar-color);
}
*::-webkit-scrollbar-track {
  background-color: transparent;
}
input::placeholder {
  color: var(--font-color);
}
#web_bg {
  position: fixed;
  z-index: -999;
  width: 100%;
  height: 100%;
  background-attachment: local;
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  position: relative;
  margin: 20px 0 14px;
  color: var(--text-highlight-color);
  font-weight: bold;
}
h1 code,
h2 code,
h3 code,
h4 code,
h5 code,
h6 code {
  font-size: inherit !important;
}
* {
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
}
.table-wrap {
  overflow-x: scroll;
  margin: 0 0 20px;
  border-radius: 5px;
}
.table-wrap table {
  border-radius: 5px;
}
.table-wrap table thead > tr:first-child th:first-child {
  border-top-left-radius: 5px;
}
.table-wrap table thead > tr:first-child th:last-child {
  border-top-right-radius: 5px;
}
.table-wrap table tbody > tr:last-child td:first-child {
  border-bottom-left-radius: 5px;
}
.table-wrap table tbody > tr:last-child td:last-child {
  border-bottom-right-radius: 5px;
}
table {
  display: table;
  width: 100%;
  border-spacing: 0;
  border-collapse: separate;
  border-top: 1px solid var(--light-grey);
  border-left: 1px solid var(--light-grey);
  empty-cells: show;
}
table thead {
  background: rgba(153,169,191,0.1);
}
table th,
table td {
  padding: 6px 12px;
  border: 1px solid var(--light-grey);
  border-top: none;
  border-left: none;
  vertical-align: middle;
}
*::selection {
  background: #00c4b6;
  color: #f7f7f7;
}
button {
  padding: 0;
  outline: 0;
  border: none;
  background: none;
  cursor: pointer;
  touch-action: manipulation;
}
a {
  color: #99a9bf;
  text-decoration: none;
  word-wrap: break-word;
  -webkit-transition: all 0.2s;
  -moz-transition: all 0.2s;
  -o-transition: all 0.2s;
  -ms-transition: all 0.2s;
  transition: all 0.2s;
  overflow-wrap: break-word;
}
a:hover {
  color: #49b1f5;
}
.text-center {
  text-align: center;
}
.text-right {
  text-align: right;
}
img[src=''],
img:not([src]) {
  opacity: 0;
  -ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=0)";
  filter: alpha(opacity=0);
}
.img-alt {
  margin: -10px 0 10px;
  color: #858585;
}
.img-alt:hover {
  text-decoration: none !important;
}
blockquote {
  margin: 0 0 20px;
  padding: 7px 15px;
  border-left: 4px solid #49b1f5;
  background-color: var(--blockquote-bg);
  color: var(--blockquote-color);
  border-radius: 6px;
}
blockquote footer cite:before {
  padding: 0 5px;
  content: '—';
}
blockquote > :last-child {
  margin-bottom: 0 !important;
}
