}
.read-mode #page-header.post-bg > #post-info {
  text-align: center;
}
.read-mode #post {
  margin: 0 auto;
  background: transparent;
  -webkit-box-shadow: none;
  box-shadow: none;
}
.read-mode #post:hover {
  -webkit-box-shadow: none;
  box-shadow: none;
}
.read-mode > canvas {
  display: none !important;
}
.read-mode .highlight-tools,
.read-mode #footer,
